// Fetch resident details
$.getJSON('/resident/get_resident', {id: 123}, function(data) {
    console.log(data);
    $('#firstName').val(data.first_name);
    $('#lastName').val(data.last_name);
    $('#birthdate').val(data.birthdate);
});

// Update resident
$.ajax({
    url: '/resident/update_resident',
    type: 'POST',
    data: {
        id: 123,
        first_name: 'Juan',
        last_name: 'Dela Cruz',
        birthdate: '1990-05-15'
    },
    success: function(response) {
        if(response.success) {
            alert('Resident updated successfully!');
        }
    }
});

// Create household with residents
fetch('/household/save', {
    method: 'POST',
    headers: {'Content-Type': 'application/json'},
    body: JSON.stringify({
        household_head: 'Juan Dela Cruz',
        address: '123 Main St, Barangay San Juan',
        zone: 'Zone 1',
        purk: 'PURK 1'
    })
})
.then(response => response.json())
.then(data => console.log('Household created:', data));

// Submit certificate request
$.post('/certificate/certificate_request_submit', {
    resident_id: 123,
    certificate_type: 'Barangay Clearance',
    purpose: 'Employment requirement',
    status: 'Pending'
}, function(response) {
    console.log('Certificate requested:', response);
});

// Update certificate status
$.post('/certificate/update_status', {
    id: 456,
    status: 'Approved',
    issued_by: 10
}, function(response) {
    console.log('Certificate updated:', response);
});

// Access print page
window.location.href = '/certificate/print?id=456';

// JavaScript scale control
function applyScale(scale) {
    const cert = document.getElementById('certificateContent');
    cert.style.transform = `scale(${scale})`;
    cert.style.transformOrigin = 'top center';
}

// Create officer record
// Only one active Barangay Captain is allowed
const officerData = {
    user_id: 5,
    resident_id: 10,
    position: 'Barangay Captain',
    term_start: '2024-01-01',
    term_end: '2028-12-31',
    status: 'Active'
};

// When creating an Active Barangay Captain,
// other active captains are automatically deactivated
$.post('/admin/add_account', officerData, function(response) {
    console.log('Officer created:', response);
});

// Check user role in PHP
if($_SESSION['role'] === 'Admin') {
    // Admin-only features
    include 'admin_panel.php';
} else if($_SESSION['role'] === 'Staff') {
    // Staff features
    include 'staff_panel.php';
} else if($_SESSION['role'] === 'Tanod') {
    // Tanod-only features
    include 'tanod_panel.php';
}

// Check position
if($_SESSION['position'] === 'Barangay Captain') {
    // Captain-specific actions
}

// Login function
function requireStaff() {
    if(!isset($_SESSION['user_id']) || 
       ($_SESSION['role'] !== 'Admin' && $_SESSION['role'] !== 'Staff')) {
        header('Location: /login');
        exit;
    }
}

// Fetch dashboard data
$.get('/api/dashboard_data', function(data) {
    console.log('Total Residents:', data.residents.total);
    console.log('Households:', data.households.total);
    console.log('Certificates:', data.certificates);
    console.log('Blotter Cases:', data.blotter);

    // Update UI
    $('#residentCount').text(data.residents.total);
    $('#householdCount').text(data.households.total);
});

// Show confirmation dialog
function showConfirmDialog(title, message, callback) {
    $('<div>' + message + '</div>').dialog({
        modal: true,
        title: title,
        width: 420,
        buttons: {
            'Yes': function() {
                $(this).dialog('close');
                callback(true);
            },
            'No': function() {
                $(this).dialog('close');
                callback(false);
            }
        }
    });
}

// Show info dialog
function showDialog(title, message) {
    $('<div>' + message + '</div>').dialog({
        modal: true,
        title: title,
        width: 420,
        buttons: {
            'Ok': function() {
                $(this).dialog('close');
            }
        }
    });
}

// Validate form fields
function validateForm() {
    let errors = [];

    const firstName = $('#first_name').val().trim();
    if(!firstName) {
        errors.push('First name is required');
    }

    const email = $('#email').val().trim();
    if(email && !isValidEmail(email)) {
        errors.push('Invalid email format');
    }

    if(errors.length > 0) {
        alert(errors.join('\n'));
        return false;
    }
    return true;
}

// Validate email
function isValidEmail(email) {
    const regex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return regex.test(email);
}

